package practical;

public class p9 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//sum of even and odd
		int a = 90;
		int b = 30;
		int c = a+b;
		if(c%2 == 0) {
			System.out.println("sum is even:"+c);
		}else {
			System.out.println("sum is odd:"+c);
		}

	}

}
